import React, { useEffect, useRef } from "react";
import styles from "./TeamFilters.module.css";

const TeamFilters = ({ teamLogo, teamName, onFilterSelect }) => {
  const filterRef = useRef();
  const startX = useRef(0); // useRef로 startX를 관리

  const onScrollStart = (e) => {
    startX.current = e.pageX || e.touches[0].pageX;
    filterRef.current.addEventListener("mousemove", onScrollMove);
    filterRef.current.addEventListener("mouseup", onScrollEnd);
    filterRef.current.addEventListener("touchmove", onScrollMove);
    filterRef.current.addEventListener("touchend", onScrollEnd);
  };

  const onScrollMove = (e) => {
    const x = e.pageX || e.touches[0].pageX;
    const walk = startX.current - x; // startX.current 사용
    filterRef.current.scrollLeft += walk;
    startX.current = x; // startX.current 업데이트
  };

  const onScrollEnd = () => {
    filterRef.current.removeEventListener("mousemove", onScrollMove);
    filterRef.current.removeEventListener("mouseup", onScrollEnd);
    filterRef.current.removeEventListener("touchmove", onScrollMove);
    filterRef.current.removeEventListener("touchend", onScrollEnd);
  };

  useEffect(() => {
    const ref = filterRef.current;
    ref.addEventListener("mousedown", onScrollStart);
    ref.addEventListener("touchstart", onScrollStart);

    return () => {
      ref.removeEventListener("mousedown", onScrollStart);
      ref.removeEventListener("touchstart", onScrollStart);
    };
  }, []);

  return (
    <div className={styles.TeamFilters} ref={filterRef}>
      <div
        className={styles.filterItem}
        onClick={() => onFilterSelect(teamName)}
      >
        <img
          src={teamLogo}
          alt={`${teamName} Icon`}
          className={styles.teamIcon}
        />
        <div className={styles.teamName}>{teamName}</div>
      </div>
    </div>
  );
};

export default TeamFilters;
