import React, { useState, useEffect, useRef } from "react";
import Header from "../components/Home/Header";
import styles from "./Schedule.module.css";
import TeamFilters from "../components/Schedule/TeamFilters";

// Images
import HLE from "../assets/Home/HLE.png";
import GEN from "../assets/Home/GEN.png";
import T1 from "../assets/Home/T1.png";
import DK from "../assets/Home/DK.png";
import KT from "../assets/Home/KT.png";
import BFX from "../assets/Home/BFX.png";
import NS from "../assets/Home/NS.png";
import DNF from "../assets/Home/DNF.png";
import DRX from "../assets/Home/DRX.png";
import BRO from "../assets/Home/BRO.png";
import MENU from "../assets/menu.png";
import leftArrow from "../assets/Home/leftArrow.png";
import rightArrow from "../assets/Home/rightArrow.png";
import calendar from "../assets/calendar.png";

const Schedule = () => {
  const [currentDate, setCurrentDate] = useState(new Date(2025, 0));
  const teams = [
    { logo: MENU, name: "전체" },
    { logo: T1, name: "T1" },
    { logo: HLE, name: "HLE" },
    { logo: GEN, name: "GEN" },
    { logo: DK, name: "DK" },
    { logo: KT, name: "KT" },
    { logo: BFX, name: "BFX" },
    { logo: DNF, name: "DNF" },
    { logo: NS, name: "NS" },
    { logo: DRX, name: "DRX" },
    { logo: BRO, name: "BRO" },
  ];

  const calendarRef = useRef(null); // 캘린더 tbody 참조를 위한 ref

  const handlePrevMonth = () => {
    setCurrentDate(
      new Date(currentDate.getFullYear(), currentDate.getMonth() - 1)
    );
  };

  const handleNextMonth = () => {
    setCurrentDate(
      new Date(currentDate.getFullYear(), currentDate.getMonth() + 1)
    );
  };

  const handleToday = () => {
    setCurrentDate(new Date());
  };

  useEffect(() => {
    generateCalendar(currentDate.getFullYear(), currentDate.getMonth());
  }, [currentDate]);

  const generateCalendar = (year, month) => {
    if (!calendarRef.current) return; // ref가 null인 경우 리턴
    const calendarBody = calendarRef.current;
    const firstDay = new Date(year, month, 1).getDay();
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    let html = "";
    let date = 1;

    for (let i = 0; i < 6; i++) {
      let row = "<tr>";
      for (let j = 0; j < 7; j++) {
        if (i === 0 && j < firstDay) {
          row += "<td></td>";
        } else if (date > daysInMonth) {
          break;
        } else {
          row += `<td>${date}</td>`;
          date++;
        }
      }
      row += "</tr>";
      html += row;
      if (date > daysInMonth) break;
    }

    calendarBody.innerHTML = html;

    const exampleEvents = [
      { date: 8, time: "17:00", match: "T1 vs KT" },
      { date: 9, time: "19:00", match: "HLE vs GEN" },
      { date: 12, time: "15:00", match: "T1 vs BFX" },
    ];

    exampleEvents.forEach((event) => {
      const cells = calendarBody.querySelectorAll("td");
      cells.forEach((cell) => {
        if (parseInt(cell.textContent) === event.date) {
          const div = document.createElement("div");
          div.classList.add("event");
          div.textContent = `${event.time} ${event.match}`;
          cell.appendChild(div);
        }
      });
    });
  };

  return (
    <div className={styles.ScheduleContainer}>
      <Header />
      <div className={styles.ScheduleWrapper}>
        <div className={styles.ScheduleHeader}>
          <img
            src={leftArrow}
            className={styles.arrowImg}
            onClick={handlePrevMonth}
            alt="Previous Month"
          />
          <span className={styles.date}>
            {currentDate.getFullYear()}.{currentDate.getMonth() + 1}
          </span>
          <img
            src={calendar}
            className={styles.calendarImg}
            onClick={handleToday}
            alt="Today"
          />
          <img
            src={rightArrow}
            className={styles.arrowImg}
            onClick={handleNextMonth}
            alt="Next Month"
          />
        </div>
        <div className={styles.TeamFilters}>
          <img
            src={leftArrow}
            className={styles.arrowImg}
            onClick={() => console.log("Previous Teams")} // 동작 추가 필요
            alt="Previous Teams"
          />
          {teams.slice(0, 8).map((team) => (
            <TeamFilters
              key={team.name}
              teamLogo={team.logo}
              teamName={team.name}
              onFilterSelect={(teamName) => console.log(teamName)}
            />
          ))}
          <img
            src={rightArrow}
            className={styles.arrowImg}
            onClick={() => console.log("Next Teams")} // 동작 추가 필요
            alt="Next Teams"
          />
        </div>
        <table className={styles.calendar}>
          <thead>
            <tr>
              <th>일</th>
              <th>월</th>
              <th>화</th>
              <th>수</th>
              <th>목</th>
              <th>금</th>
              <th>토</th>
            </tr>
          </thead>
          <tbody ref={calendarRef}></tbody>
        </table>
      </div>
    </div>
  );
};

export default Schedule;
