import React, { useEffect, useRef } from "react";
import styles from "./TeamFilters.module.css";

const TeamFilters = ({ teamLogo, teamName, onFilterSelect }) => {
  const filterRef = useRef(null); // 스크롤 대상 요소 참조
  const isDragging = useRef(false); // 드래그 여부 상태
  const startX = useRef(0); // 시작 X 좌표
  const scrollLeft = useRef(0); // 초기 스크롤 위치

  const onScrollStart = (e) => {
    isDragging.current = true; // 드래그 상태 시작
    startX.current = e.pageX || e.touches[0].pageX; // 터치 또는 마우스 위치
    scrollLeft.current = filterRef.current.scrollLeft; // 현재 스크롤 위치 저장
  };

  const onScrollMove = (e) => {
    if (!isDragging.current) return; // 드래그 중이 아니면 무시
    const x = e.pageX || e.touches[0].pageX; // 현재 X 좌표
    const walk = (startX.current - x) * 1.5; // 스크롤 이동 거리 계산
    filterRef.current.scrollLeft = scrollLeft.current + walk; // 스크롤 위치 업데이트
  };

  const onScrollEnd = () => {
    isDragging.current = false; // 드래그 상태 종료
  };

  useEffect(() => {
    const ref = filterRef.current;
    ref.addEventListener("mousedown", onScrollStart);
    ref.addEventListener("mousemove", onScrollMove);
    ref.addEventListener("mouseup", onScrollEnd);
    ref.addEventListener("mouseleave", onScrollEnd); // 영역 벗어날 때도 종료
    ref.addEventListener("touchstart", onScrollStart);
    ref.addEventListener("touchmove", onScrollMove);
    ref.addEventListener("touchend", onScrollEnd);

    return () => {
      ref.removeEventListener("mousedown", onScrollStart);
      ref.removeEventListener("mousemove", onScrollMove);
      ref.removeEventListener("mouseup", onScrollEnd);
      ref.removeEventListener("mouseleave", onScrollEnd);
      ref.removeEventListener("touchstart", onScrollStart);
      ref.removeEventListener("touchmove", onScrollMove);
      ref.removeEventListener("touchend", onScrollEnd);
    };
  }, []);

  return (
    <div className={styles.TeamFilters} ref={filterRef}>
      <div
        className={styles.filterItem}
        onClick={() => onFilterSelect(teamName)}
      >
        <img
          src={teamLogo}
          alt={`${teamName} Icon`}
          className={styles.teamIcon}
        />
        <div className={styles.teamName}>{teamName}</div>
      </div>
    </div>
  );
};

export default TeamFilters;
